import React, { useState } from "react";
import axios from "axios";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const correctUsername = "testUser";
  const correctPassword = "testPassword";

  const handleLogin = async (e) => {
    e.preventDefault();

    // اعتبارسنجی داده‌های ورودی در سمت کلاینت
    if (username === correctUsername && password === correctPassword) {
      try {
        const response = await axios.post(
          "https://7afb70a0-e13a-401f-b015-d5658da3aefb.mock.pstmn.io/login",
          {
            username,
            password,
          }
        );

        if (response.data.success) {
          // ورود موفقیت‌آمیز
          setIsLoggedIn(true);
          setError("");
          localStorage.setItem("authToken", response.data.token);
        }
      } catch (error) {
        // مدیریت خطاها
        setError("An error occurred while trying to log in");
        console.error(error);
      }
    } else {
      // خطا در صورت نادرست بودن اطلاعات ورودی
      setError("Invalid username or password");
    }
  };

  return (
    <div>
      {isLoggedIn ? (
        <h1>Welcome!</h1>
      ) : (
        <form onSubmit={handleLogin}>
          <div>
            <label>Username:</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Login</button>
          {error && <p style={{ color: "red" }}>{error}</p>}
        </form>
      )}
    </div>
  );
};

export default Login;
